<?php defined( '_JEXEC' ) or die( 'Restricted access' ); jimport( 'joomla.application.component.controller' ); class ReferidosControllerUser extends JController{		public function buscar(){		$modelo = $this->getModel('user');		$modelo->setParams();				//filtro para plan		$id_plan = JRequest::getVar( 'id_plan' );		$modelo->addFilter( 'id_plan' , $id_plan);		$this->setRedirect( "index.php?option=com_referidos&view=usuarios" );		return;	}	public function limpiar(){		$modelo = $this->getModel('user');		$modelo->cleanParams();		$this->setRedirect( "index.php?option=com_referidos&view=usuarios" );		return;	}}