-- ------------------------------------------------------- Table `#__planes`-- -----------------------------------------------------CREATE TABLE IF NOT EXISTS `#__planes` (  `id_plan` INT NOT NULL AUTO_INCREMENT,  `nom_plan` VARCHAR(150) NOT NULL,  `descripcion` VARCHAR(200) NULL DEFAULT NULL,  `created` DATETIME NOT NULL DEFAULT '0000-00-00 00:00:00',  `created_by` INT NOT NULL,  `modified` DATETIME NOT NULL DEFAULT '0000-00-00 00:00:00',  `modified_by` INT NOT NULL,  `estado_activo` int(1) NOT NULL DEFAULT '1' COMMENT 'desactivado = 0; activo=1',  `niveles` INT NOT NULL DEFAULT 1 COMMENT 'filas de la matriz',  `personas_nivel` INT NOT NULL DEFAULT 1 COMMENT 'columnas de la matriz',  `comision_x_persona` DOUBLE NOT NULL DEFAULT 0,  `personas` INT NOT NULL DEFAULT 0,  `orden` INT NULL DEFAULT 0,  `valor_pesos` DOUBLE NULL DEFAULT 0,  `valor_dolares` DOUBLE NULL DEFAULT 0,  `tipo_plan` CHAR(1) DEFAULT '0' COMMENT 'pago=1; no pago=0',  PRIMARY KEY (`id_plan`),  INDEX `estado_activo` (`estado_activo` ASC))ENGINE = InnoDBDEFAULT CHARACTER SET = utf8COLLATE = utf8_spanish_ci;-- ------------------------------------------------------- Table `#__users_planes`-- -----------------------------------------------------CREATE TABLE IF NOT EXISTS `#__users_planes` (  `id_user_plan` INT NOT NULL AUTO_INCREMENT,  `id_user` INT NOT NULL,  `id_plan` INT NOT NULL,  `estado_plan` CHAR(1) NOT NULL DEFAULT '1' COMMENT 'activo = 1; desactivado = 0; vencido = 2',  `fecha_adquiere` DATETIME NULL DEFAULT '0000-00-00 00:00:00',  `fecha_vence` DATETIME NULL DEFAULT '0000-00-00 00:00:00',  `fecha_vencio` DATETIME NULL DEFAULT '0000-00-00 00:00:00',  PRIMARY KEY (`id_user_plan`),  INDEX `id_user` (`id_user` ASC),  INDEX `id_plan` (`id_plan` ASC))ENGINE = InnoDBDEFAULT CHARACTER SET = utf8COLLATE = utf8_spanish_ci;-- ------------------------------------------------------- Table `#__caracteristicas`-- -----------------------------------------------------CREATE TABLE IF NOT EXISTS `#__caracteristicas` (  `id_caracteristica` INT NOT NULL AUTO_INCREMENT,  `caracteristicas` VARCHAR(200) NOT NULL,  `descripcion` VARCHAR(200) NULL DEFAULT NULL,  `estado_activo` CHAR(1) NOT NULL DEFAULT '1' COMMENT 'desactivado = 0 ; activado =1',  `tipo_caracteristica` CHAR(1) NULL COMMENT 'fotos = 1; vídeos =2 ; eventos = 3 ',  `points_cumple` INT NULL DEFAULT 0 COMMENT 'si cumple con la característica puede obtener sierta cantidad de puntos',  `created` DATETIME NOT NULL DEFAULT '0000-00-00 00:00:00',  `created_by` INT NOT NULL,  `modified` DATETIME NOT NULL DEFAULT '0000-00-00 00:00:00',  `modified_by` INT NOT NULL,  `show_home` CHAR(1) NULL DEFAULT '0' COMMENT 'sale en  el home = 1 ; no sale  = 0',  `path_icono` VARCHAR(250) NULL,  `icono` VARCHAR(200) NULL,  PRIMARY KEY (`id_caracteristica`))ENGINE = InnoDBDEFAULT CHARACTER SET = utf8COLLATE = utf8_spanish_ci;-- ------------------------------------------------------- Table `#__caracteristicas_planes`-- -----------------------------------------------------CREATE TABLE IF NOT EXISTS `#__caracteristicas_planes` (  `id_caracteristica_plan` INT NOT NULL AUTO_INCREMENT,  `id_plan` INT NOT NULL,  `id_caracteristica` INT NOT NULL,  `valor` VARCHAR(65) NULL,  `tipo` VARCHAR(45) NULL,  `created` DATETIME NULL DEFAULT '0000-00-00 00:00:00',  `created_by` INT NULL,  PRIMARY KEY (`id_caracteristica_plan`),  INDEX `id_plan` (`id_plan` ASC),  INDEX `id_caracteristica` (`id_caracteristica` ASC))ENGINE = InnoDBDEFAULT CHARACTER SET = utf8COLLATE = utf8_spanish_ci;-- ------------------------------------------------------- Table `#__user_comisiones`-- -----------------------------------------------------CREATE TABLE IF NOT EXISTS `#__user_comisiones` (  `id_user_comision` INT NOT NULL AUTO_INCREMENT,  `id_user_plan` INT NOT NULL,  `valor_comision` DOUBLE NULL DEFAULT 0,  `estado_pago` CHAR(1) NULL DEFAULT '0' COMMENT 'pagado = 1; no pagado = 0',  `fecha_corte` DATETIME NULL DEFAULT '0000-00-00 00:00:00',  `fecha_pago` DATETIME NULL DEFAULT '0000-00-00 00:00:00',  `id_comi_gen` INT NOT NUll 'hace referencia a la comision general'  PRIMARY KEY (`id_user_comision`),  INDEX `id_user` (`id_user_plan` ASC),  INDEX `estado_pago` (`estado_pago` ASC))ENGINE = InnoDBDEFAULT CHARACTER SET = utf8COLLATE = utf8_spanish_ci;-- ------------------------------------------------------- Table `#__referidos_user`-- -----------------------------------------------------CREATE TABLE IF NOT EXISTS `#__referidos_user` (  `id_referidos_user` INT NOT NULL AUTO_INCREMENT,  `id_user_plan` INT NOT NULL,  `id_referido` INT NOT NULL,  `fecha_registro` DATETIME NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT 'fecha en la que se le asigno el referido',  `id_referido_por` INT NULL DEFAULT 0 COMMENT 'otro referido refiere al actual',  `estado_ref` CHAR(1) NOT NULL DEFAULT '1' COMMENT 'valido = 1; no valido = 0 para comision',  `directo` CHAR(1) NOT NULL COMMENT 'directo = 1; indirecto = 2',  `nivel` INT NOT NULL COMMENT 'nivel de la matriz',  `forcomision` CHAR(1) NULL DEFAULT NULL COMMENT 'para comision = 0, ya en comision = 1',  `tipo_plan` CHAR(1) NULL DEFAULT NULL COMMENT 'mismo plan=1; otro plan = 2',  PRIMARY KEY (`id_referidos_user`))ENGINE = InnoDBDEFAULT CHARACTER SET = utf8COLLATE = utf8_spanish_ci;-- ------------------------------------------------------- Table `#__invitados_ref`-- -----------------------------------------------------CREATE TABLE IF NOT EXISTS `#__invitados_ref` (  `id_invitado` INT NOT NULL AUTO_INCREMENT,  `id_user` INT NOT NULL,  `correo_invt` VARCHAR(200) NOT NULL,  `nombre_invt` VARCHAR(200) NULL,  `tipo_invt` CHAR(1) NULL DEFAULT 0 COMMENT 'se puede manejar el tipo que se requiera ( Ejem: facebook = 2, correo = 1)',  `fecha_invt` DATETIME NOT NULL DEFAULT '0000-00-00 00:00:00',  `estado_invt` CHAR(1) NOT NULL COMMENT 'pendiente = 0; confirmado = 1, pasado de tiempo=2',  `id_facebook` VARCHAR(200) NOT NULL COMMENT 'id del usuario de facebook',  `plan_invt` INT NULL,  PRIMARY KEY (`id_invitado`))ENGINE = InnoDBDEFAULT CHARACTER SET = utf8COLLATE = utf8_spanish_ci;-- ------------------------------------------------------- Table `#__conf_comisiones`-- -----------------------------------------------------CREATE TABLE IF NOT EXISTS `#__conf_comisiones` (  `id` INT NOT NULL AUTO_INCREMENT,  `semana_corte` INT NULL DEFAULT 0,  `dia_corte` INT NULL DEFAULT 0,  `semana_pago` INT NULL DEFAULT 0,  `dia_pago` INT NULL DEFAULT 0,  `semana_corte2` INT NULL DEFAULT 0,  `dia_corte2` INT NULL DEFAULT 0,  `semana_pago2` INT NULL DEFAULT 0,  `dia_pago2` INT NULL DEFAULT 0,  `email_admin` VARCHAR(200) NULL,  PRIMARY KEY (`id`))ENGINE = InnoDB;-- ------------------------------------------------------- Table `#__orden_plan`-- -----------------------------------------------------CREATE TABLE IF NOT EXISTS `#__orden_plan` (  `id` INT NOT NULL AUTO_INCREMENT,  `orden` INT NOT NULL DEFAULT 0,  `id_user` INT NOT NULL,  `id_plan` INT NOT NULL,  `created` DATETIME NULL DEFAULT '0000-00-00 00:00:00',  `tiempo_plan` INT  DEFAULT 0 COMMENT 'tiempo de la suscripcion en dias',  `estado` CHAR(1) DEFAULT '0' COMMENT 'inactivo = 0 , activo = 1',  UNIQUE (`orden`),  PRIMARY KEY (`id`))ENGINE = InnoDB;-- ------------------------------------------------------- Table `#__comision_general`-- -----------------------------------------------------CREATE TABLE IF NOT EXISTS `#__comision_general` (  `id_comision` int(11) NOT NULL AUTO_INCREMENT,  `fecha_inicia` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',  `fecha_termina` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',  `estado_pago` char(1) DEFAULT NULL COMMENT 'pagado = 1; no pagado = 0',  `valor_total` double NOT NULL DEFAULT '0',  `num_comision` int(11) NOT NULL,  `fecha_pago` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',  `fecha_genera` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',  PRIMARY KEY (`id_comision`)) ENGINE=InnoDB DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;