<?php// Joomla calls and runtimesdefined( '_JEXEC' ) or die( 'Restricted access' );jimport( 'joomla.application.component.model' );// Begining of the controllerclass ReferidosModelCaracteristica extends ReferidosModelDefault{		/**	* Object id	* var int	*/	public $id_caracteristica;		/**	* estado de activo de la caracteristica	* var string	*/	public $estado_activo;		/**	* nombre de la caracteristica	* var string	*/	public $caracteristicas;		/**	* 	* var string	*/	public $descripcion;		/**	* fotos = 1; vídeos =2 ; eventos = 3 etc	* var string	*/	public $tipo_caracteristica;		/**	* estado de activo de la caracteristica	* var string	*/	public $points_cumple;		/**	* fecha de creación	* var date	*/	public $created;		/**	* creador de la caracteristica	* var int	*/	public $created_by;		/**	* fecha de creación	* var date	*/	public $modified;		/**	* creador de la caracteristica	* var int	*/	public $modified_by;		/**	* sale en  el home = 1 ; no sale  = 0	* var string	*/	public $show_home;	/**	* ruta de la imagen cargado	* var string	*/	public $path_icono;		/**	* nombre de la imagen con extencion	* var string	*/	public $icono;					/**	 * redefined Constant for table 	 * @var string	 */	const TABLE = '#__caracteristicas';		//constante base para los filtros	const FILTER_STATE = 'filter.referidos.caracteristica.';		/**	 * Attributes Map	 * @var array	 */	public $attrs_map = array( 			'id_caracteristica'		,	'estado_activo'			,	'caracteristicas'		,	'descripcion'		,	'tipo_caracteristica'		,	'points_cumple'		,	'created'		,	'created_by'		,	'modified'		,	'modified_by'		,	'show_home'		,	'path_icono'		,	'icono'		);		/**	 * Methods	 * 	 */		/**	* cambiar el estado de visualizacion en el home	* @params { String } el tipo de mensaje que devuelve	* @return { String }	*/	public function setShow( $return = 'string' ){		$db = JFactory::getDbo();				$this->show_home = ( $this->show_home == 1 ) ? 0 : 1;		// Update		$std = new stdClass();		$std->id_caracteristica = $this->id_caracteristica;		$std->show_home = $this->show_home;		if (! $db->updateObject( self::TABLE, $std, 'id_caracteristica' , false ) ) {						if( $return == 'string' ){				return "No se pudo actualizar el object. " . $db->stderr();			}			if( $return == 'bool' ){				return false;			}			if( $return == 'object' ){				$response->status = false;				$response->error = "No se pudo actualizar el object. " . $db->stderr();				return $response;			}		}		if( $return == 'string' ){			return "";		}		if( $return == 'bool' ){			return true;		}		if( $return == 'object' ){			$response->status = true;			$response->error = "";			return $response;		}	}}?>