<?php// Joomla calls and runtimesdefined( '_JEXEC' ) or die( 'Restricted access' );jimport( 'joomla.application.component.model' );// Begining of the controllerclass ReferidosModelComision extends ReferidosModelDefault{		/**	* Object id "id_user_comision"	* var int	*/	public $id_user_comision;		/**	* pagado = 1; no pagado = 0	* var char	*/	public $estado_pago;		/**	* user_plan id	* var int	*/	public $id_user_plan;		/**	* valor que comisiona el usuario	* var float	*/	public $valor_comision;	/**	* fecha de corte	* var date	*/	public $fecha_corte;		/**	* fecha de pago	* var date	*/	public $fecha_pago;		/**	* id de la remision general a que pertenece	* var int	*/	public $id_comi_gen;			/**	 * redefined Constant for table 	 * @var string	 */	const TABLE = '#__user_comisiones';		//constante base para los filtros	const FILTER_STATE = 'filter.referidos.comision.';		/**	 * Attributes Map	 * @var array	 */	protected $attrs_map = array( 			'id_user_comision'		,	'estado_pago'			,	'id_user_plan'		,	'valor_comision'		,	'fecha_corte'		,	'fecha_pago'		,	'id_comi_gen'	);		public function getConfComision(){				$db = JFactory::getDbo();		$query = $db->getQuery(true);				$query->select( '*' );		$query->from( '#__conf_comisiones AS a' );				$db->setQuery( $query );				return $db->loadObject();	}	}?>