                  <th>Nombre</th>                    <th>Cedula</th>                    <th>Cant. Referidos</th>                    <th>Valor</th>                </tr>            </thead>            <tbody>            <?php                 $totalRemision = array();            if( $this->comision_excel->usercomisiones ):                foreach( $this->comision_excel->usercomisiones as $key=>$comision ):?>                <tr>                    <td><?php echo ucfirst( $comision->name ).' '.ucfirst( $comision->apellidos ) ?></td>                    <td>{ cedula }</td>                    <td></td>                    <td><?php 						$suma_valor = array();						foreach( $comision->comisionesuser as $pos=>$comiuser ){							$suma_valor[] = ( float ) $comiuser->valor_comision;						}						unset( $comiuser );                        $suma_valor = array_sum( $suma_valor );						                        $totalRemision[] = $suma_valor;                        echo '$ '.number_format( $suma_valor , 2 , ',' , '.');                    ?></td>                </tr>            <?php endforeach; unset($comision); endif;?>                </tbody>            <tfoot>                <tr>                    <td colspan="3">Total</td>                    <td><?php                         $totalRemision = array_sum( $totalRemision );                         echo '$ '.number_format( $totalRemision , 2 , ',' , '.' );                    ?></td>                </tr>            </tfoot>        </table>    </div></div>