			<th width="20">						<input type="checkbox" name="checkall-toggle" value="" title="<?php echo JText::_('JGLOBAL_CHECK_ALL'); ?>" onclick="Joomla.checkAll(this)" />					</th>                    <th class="nowrap">Plan</th>                    <th class="nowrap">Descripción</th>                    <th class="nowrap">Activo</th>                    <th class="nowrap">Tipo Plan</th>                    <th class="nowrap">Niveles</th>                    <th class="nowrap">Personas Por Nivel</th>                    <th class="nowrap">Comisión Por Persona</th>                    <th class="nowrap">Valor Pesos</th>                    <th class="nowrap">Valor Dolar</th>                    <th class="nowrap">Orden</th>                    <th class="nowrap">Creado</th>                    <th class="nowrap">Creado Por</th>                    <th class="nowrap">ID</th>				</tr>			</thead>						<!-- Elementos -->			<tbody>				<?php					$n = 0;					//loop for the elements										foreach ( $this->items as $i => $item ):				?>				<tr>                	<td class="center"> 						<?php                             // Create the checkboxes                        	echo JHtml::_('grid.id', $n, $item->id_plan); 						?>					</td>					<td class="center"><a href="<?php echo JRoute::_( 'index.php?option=com_referidos&view=createplan&layout=edit&id='.$item->id_plan, false)?>"><?php echo $item->nom_plan?></a></td>					<td class="left"><?php echo $item->descripcion?></td>					<td class="center">                    	<a class="jgrid" href="javascript:void(0);" onclick="cambiarEstado(<? echo ( int) $item->estado_activo; ?>,<? echo ( int ) $item->id_plan; ?>,this)" title="Publicar Elemento"><span  class="state <?php echo ( $item->estado_activo == '1' ) ? "publish" : "unpublish"; ?>"><span class="text">Despublicado</span></span></a>                    </td>                    <td class="center"><?php 						$tipo_pago = '';						switch( (int) $item->tipo_plan ){							case 0 : $tipo_pago = 'Gratuito'; break;							case 1 : $tipo_pago = 'Pago'; break;						}						echo $tipo_pago?></td>					<td class="center"><?php echo $item->niveles?></td>                    <td class="center"><?php echo $item->personas_nivel?></td>					<td class="center"><?php echo '$ '.number_format( $item->comision_x_persona, 2, ',' , '.' )?></td>                    <td class="center"><?php echo '$ '.number_format( $item->valor_pesos , 2 , ',' , '.')?></td>                    <td class="center"><?php echo '$ '.number_format( $item->valor_dolares , 2 , ',' , '.')?></td>					<td class="center"><?php echo $item->orden?></td>                    <td class="center"><?php echo date( 'd/m/Y' , strtotime( $item->created ) )?></td>                    <td class="center"><?php echo $item->usercreate->name?></td>                    <td class="center"><?php echo $item->id_plan?></td>				</tr>				<?php $n++; endforeach; ?>			</tbody>            <!--Paginador-->            <tfoot>            	<tr>                	<td colspan="13">                    	<?php 							$limit = JRequest::getInt( 'limit' , 10);							$limitstart = JRequest::getInt( 'limitstart' , 0);																		HelperPaginador::getPagination( 'adminForm' , count( $this->itemsall ) , count( $this->items ) , $limitstart , $limit);						?>                    </td>                </tr>            </tfoot>            <!--Fin Paginador-->		</table>	</div>	<input type="hidden" name="option" value="com_referidos" />	<input type="hidden" name="task" value="" />	<input type="hidden" name="boxchecked" value="0" />	<input type="hidden" name="controller" value="plan" /></form><div class="clr"></div>