ho JRequest::getVar( 'filter_search' , '')?>" title="Buscar Caracteristica" />			<button type="submit" onclick="this.form.submit();"><?php echo JText::_('JSEARCH_FILTER_SUBMIT'); ?></button>			<button type="button" onclick="document.id('filter_search').value=''; this.form.submit();">Limpiar</button>		</div>        <div class="filter-select fltrt">        	<ul class="list-filtro">            	<li>                	<label for="filter_state"><?php echo JText::_('Filtrar Usuario'); ?></label>                </li>                <li>               		<?php $this->selectPlan( $filterplan );?>                </li>            </ul>		</div>	</fieldset><!--fin filtro-->    	<div class="clr"> </div>       <div class="editcell">		<table class="adminlist">			<!-- Encabezado -->			<thead>				<tr>					<th width="20">						<input type="checkbox" name="checkall-toggle" value="" title="<?php echo JText::_('JGLOBAL_CHECK_ALL'); ?>" onclick="Joomla.checkAll(this)" />					</th>                    <th class="nowrap">Nombre</th>                    <th class="nowrap">Username</th>                    <th class="nowrap">Estado</th>                    <th class="nowrap">E-mail</th>                    <th class="nowrap">Plan</th>                    <th class="nowrap">Invitados</th>                    <th class="nowrap">Referidos</th>                    <th class="nowrap">Comisi&oacute;n</th>				</tr>			</thead>						<!-- Elementos -->			<tbody>				<?php					$n = 0;					//loop for the elements									foreach ( $this->items as $i => $item ):				?>				<tr>                	<td class="center"> 						<?php                             // Create the checkboxes                        	echo JHtml::_('grid.id', $n, $item->userid); 						?>					</td>                    <td class="center"><a href="<?php echo JRoute::_( 'index.php?index.php?option=com_users&view=users&id='.$item->id );?>"><?php echo $item->name.' '.$item->apellidos?></a></td>                