<?php// Joomla calls and runtimesdefined( '_JEXEC' ) or die( 'Restricted access' );jimport( 'joomla.application.component.model' );// Begining of the controllerclass ReferidosModelComisionGeneral extends ReferidosModelDefault{		/**	* Object id "id_user_comision"	* var int	*/	public $id_comision;		/**	*estado de pago	* var char	*/	public $estado_pago;		/**	* fecha de inicio del periodo liquidado	* var date	*/	public $fecha_inicia;		/**	* fecha de final del periodo liquidado	* var date	*/	public $fecha_termina;		/**	* valor todal de la comision	* var float	*/	public $valor_total;		/**	* numero consecutivo de la comision	* var int	*/	public $num_comision;		/**	* fecha de pago	* var date	*/	public $fecha_pago;		/**	* fecha que se genera la comision con el cronjob	* var date	*/	public $fecha_genera;			/**	 * redefined Constant for table 	 * @var string	 */	const TABLE = '#__comision_general';		//constante base para los filtros	const FILTER_STATE = 'filter.referidos.comisiongeneral.';		/**	 * Attributes Map	 * @var array	 */	protected $attrs_map = array( 			'id_comision'		,	'estado_pago'			,	'fecha_inicia'		,	'fecha_termina'		,	'valor_total'		,	'num_comision'		,	'fecha_pago'		,	'fecha_genera'	);		public function getConfComision(){				$db = JFactory::getDbo();		$query = $db->getQuery(true);				$query->select( '*' );		$query->from( '#__conf_comisiones AS a' );				$db->setQuery( $query );				return $db->loadObject();	}	}?>