<?php // No direct access to this filedefined('_JEXEC') or die('Restricted access');$toolbar = CToolbarLibrary::getInstance();echo '<div id="community-wrap">'.$toolbar->getHTML().'</div>';?><div class="content-referidos"><?php if( $this->tieneplan )	echo '<div class="content-barra"><h4>Activar Otro Plan</h4>' .ToolBarReferidos::render( 'Activar Otro Plan' ). '</div>';?>    <h4 class="til-referidos">Planes</h4>    <div class="datagrid datagrid-head">        <table>            <thead>                <tr>                    <th class="datagrid-center" style="width:55%"><span>Caracteristicas del Plan</span></th>                    <?php foreach( $this->planes as $key=>$plan):?>                    <th class="datagrid-center" style="width:15%"><span><?php echo $plan->nom_plan?></span></th>                    <?php endforeach; unset( $plan );?>                <tr>                </thead>        </table>    </div>    <div class="datagrid">        <table>            <tbody>            	<?php foreach( $this->caracteristicas as $key=>$caract ):?>                <tr>                    <td class="datagrid-left" style="width:55%"><?php echo $caract->caracteristicas?></td>                    <?php foreach( $this->planes as $key=>$plan ):?>                    <td class="datagrid-center" style="width:15%"><?php $obj = $this->caracteristicaPlan( (int) $plan->id_plan, (int) $caract->id_caracteristica ); echo $obj->valor?></td>                    <?php endforeach; unset( $plan );?>                </tr>                <?php endforeach;?>            </tbody>            <tfoot>            	<tr>                	<td style="width:55%">&nbsp;</td>                    <?php foreach( $this->planes as $key=>$plan ):?>                    <td class="datagrid-center" style="width:15%"><a href="<?php echo JRoute::_( 'index.php?option=com_referidos&task=plan.ordenplan&id_plan='. (int) $plan->id_plan )?>" class="button-activa">Activar Ahora</a></td>                    <?php endforeach; unset($plan);?>                </tr>                <tr>                	<td colspan="<?php echo count( $this->planes )+1?>"></td>                </tr>            </tfoot>        </table>    </div></div>