<?php // No direct access to this filedefined('_JEXEC') or die('Restricted access');$toolbar = CToolbarLibrary::getInstance();echo '<div id="community-wrap">'.$toolbar->getHTML().'</div>';$invitados  = $this->getInvitadosUser();$planes = $this->planes();$mienbrosall = ( count($this->ref_directos_all) + count( $this->ref_indirectos_all ) );?><div class="content-referidos"><?php 	echo '<div class="content-barra"><h4>Seguimiento de Referidos</h4>' .ToolBarReferidos::render( 'Seguimiento de Referidos' ). '</div>';?>	<div class="infaccount">    	<ul>        	<li><img src="<?php echo $this->user_data->avatar;?>" class="img-infuser" alt="<?php echo $this->user_data->name.' '.$this->user_data->apellidos ?>"/></li>            <li>            	<ul class="data-listinf">                	<li><span>Miembro desde <?php echo date( 'd-m-Y' , strtotime( $this->user_data->registerDate ) );?><span></li>                    <li><span>Usuarios Invitados:</span> <span class="til-blue"><?php echo count( $invitados );?></span></li>                    <?php foreach( $planes as $key=>$plan):?>                    <li>                    	<span>Usuarios <?php echo $plan->nom_plan?>:</span>                        <span class="til-blue"><?php 							$contador = 0;							foreach( $this->ref_directos_all as $key=>$directo ){								if( $plan->id_plan == $directo->id_plan )									$contador++;							}							foreach( $this->ref_indirectos_all as $key=>$indirecto ){								if( $plan->id_plan == $indirecto->id_plan )									$contador++;							}														echo $contador;						?></span>                   	</li>                    <?php endforeach;?>                </ul>            </li>        </ul>        <div class="data-infuser">        	<h4 class="til-referidos"><?php echo $this->user_data->name.' '.$this->user_data->apellidos ?></h4>            <a href="javascript:history.back(0);">Volver</a>            <span><?php echo $this->user_data->email;?></span>                    </div>    </div></div>