<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><head><meta http-equiv="Content-Type" content="text/html; charset=UTF-8" /><link href='http://fonts.googleapis.com/css?family=Cabin+Condensed:400,500,600,700' rel='stylesheet' type='text/css'><link href='http://fonts.googleapis.com/css?family=PT+Sans+Caption:400,700' rel='stylesheet' type='text/css'><link rel="stylesheet" type="text/css" media="screen" href="css/screen.css" /><script src=" https://ajax.googleapis.com/ajax/libs/jquery/1.7.2/jquery.js" type="text/javascript"></script><script src="jquery.validate.js" type="text/javascript"></script><script type="text/javascript">						function valida_envia(){										if(document.fvalida.nombre.value.length==0){						alert("Por Favor escriba su nombre");						document.fvalida.nombre.focus();						return false;					}										if(document.fvalida.email.value.indexOf('@')==-1){					alert("La direccion de email no es correcta");					document.fvalida.email.focus();					return false;					}										if(document.fvalida.apellido.value.length==0){						alert("Por Favor escriba su apellido");						document.fvalida.apellido.focus();						return false;					}																if(document.fvalida.mensaje.value.length==0){						alert("Por Favor escriba su mensaje");						document.fvalida.mensaje.focus();						return false;					}													document.fvalida.submit();														}</script><style>* {	color: #4e78c2;	text-align: justify;	font-family: 'PT Sans Caption', sans-serif;	font-weight: bolder;	font-size: 14px;}.error { color:#C00;  }em { color:#C00; }input[type='text']{ background-color: #FFFFFF;    color: #333 ;    border: 1px solid #CCCCCC;	border-radius:2px;     font-size: 14px;	font-family: 'Cabin Condensed', sans-serif;    height: 25px;	font-weight:normal;    margin-bottom: 1px;	float:left;    margin-right: 20%;    margin-bottom: 10px;    padding-left: 5px;    text-align: left;    width: 70%;	-webkit-box-shadow: 2px 2px 16px rgba(50, 50, 50, 0.55);    -moz-box-shadow:    3px 3px 3px rgba(50, 50, 50, 0.55);    box-shadow:         2px 2px 16px rgba(50, 50, 50, 0.55);}input[type='submit']{ 	background-color: transparent;	background-image: url("btn_enviar.png");	background-repeat: no-repeat;	border: medium none;	color: transparent;	cursor: pointer;	font-family: 'Cabin Condensed', sans-serif;	font-size: 14px;	height: 60px;	padding-top: 0;	text-align: center;	width: 35%;	margin-top: 22px;	margin-bottom: 5px;	margin-right: 2%;}input[type='reset']{ background-color: transparent;    background-color: transparent;	background-image: url("btn_borrar.png");	background-repeat: no-repeat;	border: medium none;	color: transparent;	cursor: pointer;	font-family: 'Cabin Condensed', sans-serif;	font-size: 14px;	height: 60px;	padding-top: 0;	text-align: center;	width: 16%;	margin-top: 20px;	margin-right: 0%;}textarea{	color: #333 ;	background-color: #FFFFFF;	border: 1px solid #CCCCCC;	border-radius:2px;    font-family: 'Cabin Condensed', sans-serif;    font-size: 14px;	font-weight:normal;    margin-bottom: 1px;    margin-right: 20%;	float:left;    padding-bottom: 1px;    padding-left: 5px;    text-align: left;    width: 70%;	height: 70px; !important;	-webkit-box-shadow: 2px 2px 16px rgba(50, 50, 50, 0.55);    -moz-box-shadow:    3px 3px 3px rgba(50, 50, 50, 0.55);    box-shadow:         2px 2px 16px rgba(50, 50, 50, 0.55);}#commentForm { }#commentForm label { width:114px; height:10px;}#commentForm label.error { margin-left: 6px; }</style><body><form id="commentForm"  name="fvalida" method="post" action="procesar.php" onSubmit="return valida_envia()"><table style="margin-left: 10%; "width="100%" height="auto" border="0" cellpadding="0" cellspacing="0" >  <tr>    <td valign="top" style="width:20%"><label for="cname">Nombre</label ></td>    <td style="width:80%"><input name="nombre" type="text" id="cname" minlength="2" /></td>  </tr>  <tr>    <td valign="top"><label for="apellido">Apellido</label ></td>    <td ><input name="apellido" type="text" id="apellido" minlength="2" /></td>  </tr>  <tr>    <td valign="top"><label for="cemail">E-mail</label></td>    <td ><input name="email" type="text" id="cemail" class="required" /></td>  </tr>  <tr>    <td valign="top"><label>Mensaje</label></td>    <td ><textarea name="mensaje" cols="20" rows="3" id="mensaje" class="required"></textarea></td>  </tr>  <tr>    <td colspan="2" style="text-align:right"><input type="reset" value="Borrar"/> <input type="submit" name="Submit" value="Enviar"  /></td>  </tr></table></form>